import React from 'react'
import withRebass from './withRebass'
import classnames from 'classnames'

/** Arrow for use in dropdowns and other UI elements */

const Arrow = ({
  direction,
  children,
  className,
  style,
  theme,
  subComponentStyles,
  ...props
}) => {
  const cx = classnames('Arrow', className)

  const sx = {
    display: 'inline-block',
    width: 0,
    height: 0,
    marginLeft: '.5em',
    verticalAlign: 'middle',
    borderRight: '.3125em solid transparent',
    borderLeft: '.3125em solid transparent',
    borderTop: direction === 'down' ? '.4375em solid' : null,
    borderBottom: direction === 'up' ? '.4375em solid' : null,
    pointerEvents: 'none',
    ...style
  }

  return (
    <div
      {...props}
      className={cx}
      style={sx} />
  )
}

Arrow.propTypes = {
  /** Direction of arrow */
  direction: React.PropTypes.oneOf(['up', 'down'])
}

Arrow.defaultProps = {
  direction: 'down'
}

Arrow._name = 'Arrow'

export default withRebass(Arrow)

// Fixed aspect ratio component

import React from 'react'
import classnames from 'classnames'
import withRebass from './withRebass'

const AspectRatio = ({
  ratio = 4 / 3,
  className,
  style,
  theme,
  subComponentStyles,
  ...props
}) => {
  const cx = classnames('AspectRatio', className)

  const sx = {
    width: '100%',
    height: 0,
    paddingBottom: `${ratio * 100}%`,
    ...style
  }

  return (
    <div
      {...props}
      className={cx}
      style={sx} />
  )
}

AspectRatio._name = 'AspectRatio'

export default withRebass(AspectRatio)

import React from 'react'
import withRebass from './withRebass'
import classnames from 'classnames'

/**
 * A circular image for displaying user avatars
 */

const Avatar = ({
  size,
  children,
  className,
  style,
  theme,
  subComponentStyles,
  ...props
}, { rebass }) => {
  const { colors } = theme

  const cx = classnames('Avatar', className)

  const sx = {
    display: 'inline-block',
    maxWidth: 'none',
    width: size,
    height: size,
    backgroundColor: colors.gray,
    borderRadius: 99999,
    ...style
  }

  return (
    <img
      {...props}
      className={cx}
      width={size}
      height={size}
      style={sx}
    />
  )
}

Avatar.propTypes = {
  /** Width and height of image in pixels */
  size: React.PropTypes.number
}

Avatar.defaultProps = {
  size: 48
}

Avatar._name = 'Avatar'

export default withRebass(Avatar)

import React from 'react'
import classnames from 'classnames'
import withRebass from './withRebass'
import baseline from './util/baseline'

/** Component for displaying small status indicators */

const Badge = ({
  className,
  style,
  theme,
  subComponentStyles,
  ...props
}) => {
  const {
    fontSizes,
    bold,
    baselineShift,
    scale,
    colors,
    borderRadius
  } = theme

  const cx = classnames('Badge', className)

  const pad = baseline(baselineShift)(scale[1])
  const xpad = props.circle ? 0 : scale[1]

  const sx = {
    fontSize: fontSizes[6],
    fontWeight: bold,
    display: 'inline-flex',
    alignItems: 'center',
    justifyContent: 'center',
    verticalAlignment: 'baseline',
    width: props.circle ? scale[2] : null,
    height: scale[2],
    ...pad,
    paddingLeft: xpad,
    paddingRight: xpad,
    overflow: 'hidden',
    borderRadius,
    color: colors.white,
    backgroundColor: colors.default,
    ...style.fill,
    ...style
  }

  return (
    <div
      {...props}
      className={cx}
      style={sx} />
  )
}

Badge._name = 'Badge'

export default withRebass(Badge)

import React from 'react'
import classnames from 'classnames'
import withRebass from './withRebass'

/**
 * Full-height banner with styling for background images
 */

const Banner = ({
  align,
  backgroundImage,
  className,
  style,
  theme,
  subComponentStyles,
  ...props
}, { rebass }) => {
  const { scale, colors, fontSizes } = theme

  const alignment = {
    left: 'flex-start',
    center: 'center',
    right: 'flex-end'
  }

  const alignItems = alignment[align]

  const cx = classnames('Banner', className)

  const sx = {
    fontSize: fontSizes[1],
    display: 'flex',
    flexDirection: 'column',
    alignItems,
    justifyContent: 'center',
    textAlign: align === 'center' ? 'center' : null,
    padding: scale[4],
    marginBottom: scale[3],
    color: colors.white,
    backgroundColor: colors.primary,
    minHeight: '100vh',
    backgroundPosition: 'center',
    backgroundSize: 'cover',
    backgroundImage: backgroundImage ? `url(${backgroundImage})` : null,
    ...style
  }

  return (
    <div
      {...props}
      className={cx}
      style={sx} />
  )
}

Banner.propTypes = {
  /** Horizontal alignment */
  align: React.PropTypes.oneOf(['left', 'center', 'right']),
  /** Background image source */
  backgroundImage: React.PropTypes.string
}

Banner.defaultProps = {
  align: 'center'
}

Banner._name = 'Banner'

export default withRebass(Banner)

import React from 'react'
import classnames from 'classnames'
import withRebass from './withRebass'

const Bar = ({
  value = 0,
  className,
  style,
  theme,
  subComponentStyles,
  ...props
}) => {
  const { scale, bold, colors } = theme
  const cx = classnames('Bar', className)

  const {
    backgroundColor = style.fill.backgroundColor || colors.muted,
    ...rootStyle
  } = style

  const p = `${value * 100}%`
  const backgroundImage = `linear-gradient(90deg, ${backgroundColor} ${p}%, transparent ${p})`

  const sx = {
    fontWeight: bold,
    padding: scale[1],
    backgroundSize: '100% 100%',
    backgroundImage,
    ...rootStyle
  }

  return (
    <div
      {...props}
      className={cx}
      style={sx} />
  )
}

Bar._name = 'Bar'

export default withRebass(Bar)

import React from 'react'
import classnames from 'classnames'
import withRebass from './withRebass'

/**
 * Generic box with visual styling
 */

const Block = ({
  borderColor,
  border,
  borderTop,
  borderRight,
  borderBottom,
  borderLeft,
  className,
  style,
  theme,
  subComponentStyles,
  ...props
}, { rebass }) => {
  const { colors } = theme

  borderColor = colors[borderColor] || borderColor || colors.primary

  const cx = classnames('Block', className)

  const sx = {
    borderStyle: border ? 'solid' : 'none',
    borderTopStyle: borderTop ? 'solid' : null,
    borderRightStyle: borderRight ? 'solid' : null,
    borderBottomStyle: borderBottom ? 'solid' : null,
    borderLeftStyle: borderLeft ? 'solid' : null,
    borderWidth: 4,
    borderColor,
    ...style
  }

  return (
    <div
      {...props}
      className={cx}
      style={sx} />
  )
}

Block.propTypes = {
  /** Border color - can either be a key from the theme colors object or any color value */
  borderColor: React.PropTypes.string,
  /** Adds a border */
  border: React.PropTypes.bool,
  /** Adds a border to the top side */
  borderTop: React.PropTypes.bool,
  /** Adds a border to the right side */
  borderRight: React.PropTypes.bool,
  /** Adds a border to the bottom side */
  borderBottom: React.PropTypes.bool,
  /** Adds a border to the left side */
  borderLeft: React.PropTypes.bool
}

Block._name = 'Block'

export default withRebass(Block)

import React from 'react'
import classnames from 'classnames'
import withRebass from './withRebass'

/**
 * Stylized blockquote element with citation link
 */

const Blockquote = ({
  source,
  href,
  children,
  className,
  style,
  theme,
  subComponentStyles,
  ...props
}) => {
  const { fontSizes, scale } = theme

  const cx = classnames('Blockquote', className)

  const sx = {
    root: {
      fontSize: fontSizes[3],
      fontStyle: 'italic',
      margin: 0,
      marginBottom: scale[2],
      ...style
    },
    p: {
      margin: 0,
      marginBottom: scale[1],
      ...subComponentStyles.p
    },
    cite: {
      fontSize: fontSizes[5],
      fontStyle: 'normal',
      ...subComponentStyles.cite
    },
    source: {
      color: 'inherit',
      ...subComponentStyles.source
    }
  }

  return (
    <blockquote
      {...props}
      className={cx}
      style={sx.root}>
      <p style={sx.p}>
        {children}
      </p>
      <cite style={sx.cite}>
        {'— '}
        <a href={href}
          style={sx.source}
          children={source} />
      </cite>
    </blockquote>
  )
}

Blockquote.propTypes = {
  /** Name of source */
  source: React.PropTypes.string,
  /** URL link to source */
  href: React.PropTypes.string
}

Blockquote._name = 'Blockquote'

export default withRebass(Blockquote)

