import React from 'react'
import classnames from 'classnames'
import withRebass from './withRebass'

/**
 * Breadcrumb navigation links
 */

const Breadcrumbs = ({
  links,
  children,
  className,
  style,
  theme,
  subComponentStyles,
  ...props
}) => {
  const { fontSizes, scale } = theme

  const cx = classnames('Breadcrumbs', className)

  const sx = {
    root: {
      fontSize: fontSizes[5],
      display: 'flex',
      marginBottom: scale[2],
      alignItems: 'center',
      ...style
    },
    spacer: {
      marginLeft: '.5em',
      marginRight: '.5em',
      ...subComponentStyles.spacer
    }
  }

  return (
    <div
      {...props}
      className={cx}
      style={sx.root}>
      {links.map((link, i) => {
        const linkStyle = {
          color: 'inherit',
          textDecoration: i === links.length - 1 ? 'none' : null
        }

        return (
          <div key={i}>
            <a
              {...link}
              style={linkStyle} />
            {i < links.length - 1 && (
              <span style={sx.spacer}>/</span>
            )}
          </div>
        )
      })}
    </div>
  )
}

Breadcrumbs.propTypes = {
  /** Array of link props */
  links: React.PropTypes.array.isRequired
}

Breadcrumbs.defaultProps = {
  links: []
}

Breadcrumbs._name = 'Breadcrumbs'

export default withRebass(Breadcrumbs)

import React from 'react'
import classnames from 'classnames'
import withRebass from './withRebass'

/**
 * A general purpose button element with customizable colors
 */

const Button = ({
  href,
  big,
  size,
  baseRef,
  style,
  className,
  theme,
  subComponentStyles,
  ...props
}) => {
  const { fontSizes, bold, scale, colors, borderRadius } = theme

  const Comp = href ? 'a' : 'button'

  const minHeight = size || scale[3]
  const width = theme.circle ? minHeight : null
  const padx = theme.circle ? 0 : scale[2]
  const pady = big ? scale[2] : scale[1]

  const cx = classnames('Button', className)

  const fillStyles = style.fill

  const sx = {
    fontFamily: 'inherit',
    fontSize: fontSizes[5],
    fontWeight: bold,
    lineHeight: `${scale[2]}px`,
    minHeight,
    width,
    textDecoration: 'none',
    display: 'inline-block',
    margin: 0,
    paddingTop: pady,
    paddingBottom: pady,
    paddingLeft: padx,
    paddingRight: padx,
    cursor: 'pointer',
    border: 0,
    borderRadius,
    color: colors.white,
    backgroundColor: colors.primary,
    ...fillStyles,
    ...style
  }

  return (
    <Comp
      {...props}
      ref={baseRef}
      href={href}
      className={cx}
      style={sx} />
  )
}

Button.propTypes = {
  /** Pass an href prop to make the Button an <a> tag instead of a <button> */
  href: React.PropTypes.string,
  /** Creates a larger button by adding padding */
  big: React.PropTypes.bool,
  /** Sets height of button in pixels */
  size: React.PropTypes.number,
  /** Creates a circle shaped button */
  circle: React.PropTypes.bool,
  /** Adds a ref to the button element */
  baseRef: React.PropTypes.func
}

Button._name = 'Button'

export default withRebass(Button)

import React from 'react'
