/** List component that accepts any child component item types */

const List = ({
  ordered,
  reset,
  flush,
  className,
  style,
  theme,
  subComponentStyles,
  children,
  ...props
}) => {
  const { scale } = theme
  const Comp = ordered ? 'ol' : 'ul'
  const cx = classnames('List', className)

  flush = flush || reset

  const sx = {
    root: {
      paddingLeft: flush ? 0 : scale[2],
      margin: 0,
      listStyle: reset ? 'none' : null
    },
    item: {
