  style,
  theme,
  subComponentStyles,
  ...props
}) => {
  const { scale, colors, borderRadius } = theme

  const cx = classnames('Panel', className)

  const fillStyle = {
    color: colors.black,
    borderColor: colors.gray,
    ...style.fill
  }

  const { borderColor } = fillStyle

  const sx = {
    padding: scale[2],
