  children,
  className,
  style,
  theme,
  subComponentStyles,
  ...props
}) => {
  const { scale, colors, borderColor, borderRadius } = theme

  const invalid = props['aria-invalid'] || props.invalid

  const cx = classnames('Select', className, {
    'isInvalid': invalid,
    'isDisabled': props.disabled,
    'isReadonly': props.readOnly
  })

  const {
    color,
    backgroundColor,
    ...rootStyle
  } = style

  const sx = {
    root: {
      display: horizontal ? 'flex' : null,
      alignItems: horizontal ? 'baseline' : null,
      marginBottom: scale[2],
      color: invalid ? colors.error : null,
      ...rootStyle
    },
    select: {
      fontFamily: 'inherit',
      fontSize: 'inherit',
      boxSizing: 'border-box',
      display: 'block',
      width: '100%',
      paddingLeft: scale[1],
      paddingRight: scale[3],
      height: scale[3],
      color: color || 'inherit',
      backgroundColor: backgroundColor || 'transparent',
      backgroundImage: 'none',
      borderWidth: 1,
      borderStyle: 'solid',
      borderColor: invalid ? colors.error : borderColor,
      borderRadius,
      MozAppearance: 'none',
      WebkitAppearance: 'none',
      ...style.fill,
      ...subComponentStyles.select
    },
    label: {
      paddingRight: horizontal ? scale[1] : null,
      minWidth: horizontal ? 96 : null,
      ...subComponentStyles.label
    },
    wrapper: {
      position: 'relative',
